#include<iostream> 

using namespace std;

int main()
 {
 int n, i, arr[50], j, temp;
 cout<<"Enter the Size (max. 50): ";
 cin>>n;
 cout<<"Enter "<<n<<" Numbers: ";
 for(i=0; i<n; i++)
 cin>>arr[i];
 cout<<"
Sorting the Array using Bubble Sort Technique..
";
 for(i=0; i<(n-1); i++)
 {
 for(j=0; j<(n-i-1); j++)
 {
 if(arr[j]>arr[j+1])
 {
 temp = arr[j];
 arr[j] = arr[j+1];
 arr[j+1] = temp;
 }
 }
 }
 cout<<"Array Sorted Successfully!";
 cout<<"The New Array is: ";
 for(i=0; i<n; i++)
 cout<<arr[i]<<" ";
 cout<<endl;
 return 0;
 }

    public class BubbleSortExample {  \n
    static void bubbleSort(int[] arr) {  \n
        int n = arr.length;  \n
        int temp = 0;  \n
         for(int i=0; i < n; i++){  \n
                 for(int j=1; j < (n-i); j++){  \n
                          if(arr[j-1] > arr[j]){  \n
                                 //swap elements  \n
                                 temp = arr[j-1];  \n
                                 arr[j-1] = arr[j];  \n
                                 arr[j] = temp;  \n
                         }  \n

                 }  \n
         }  \n

    }  \n
    public static void main(String[] args) {  \n
                int arr[] ={3,60,35,2,45,320,5};  \n

                System.out.println("Array Before Bubble Sort");  \n
                for(int i=0; i < arr.length; i++){  \n
                        System.out.print(arr[i] + " ");  \n
                }  \n
                System.out.println();  \n

                bubbleSort(arr);//sorting array elements using bubble sort  \n

                System.out.println("Array After Bubble Sort");  \n
                for(int i=0; i < arr.length; i++){  \n
                        System.out.print(arr[i] + " ");  \n
                }  \n

        }  \n
}

